function today() {
  const date = new Date();
  if (date.getUTCHours() == 0 || (date.getUTCHours() == 1 && date.getUTCMinutes() < 30)) {
    date.setDate(date.getDate() - 1);
    return date.toISOString().split('T')[0];
  }
  else {
    return date.toISOString().split('T')[0];
  }
}
function tomorrow() {
  const date = new Date();
  if (date.getUTCHours() == 0 || (date.getUTCHours() == 1 && date.getUTCMinutes() < 30))
    return date.toISOString().split('T')[0];
  else {
    date.setDate(date.getDate() + 1);
    return date.toISOString().split('T')[0];
  }
}
function yesterday() {
  const date = new Date();
  if (date.getUTCHours() == 0 || (date.getUTCHours() == 1 && date.getUTCMinutes() < 30)) {
    date.setDate(date.getDate() - 2);
    return date.toISOString().split('T')[0];
  }
  else {
    date.setDate(date.getDate() - 1);
    return date.toISOString().split('T')[0];
  }
}

