  //else if (!html_just && !past_counter)
  //  document.getElementById('just').innerHTML = '<p>–ë—ã–ª —Ä–æ–∑—ã–≥—Ä—ã—à, –Ω–æ –≤—ã –≤ –Ω–µ–º –Ω–µ —É—á–∞—Å—Ç–≤–æ–≤–∞–ª–∏</p>';
