function get_tguid_from_url() {  // https://www.sitepoint.com/get-url-parameters-with-javascript/
  const queryString = window.location.search;
  const urlParams = new URLSearchParams(queryString);
  const id = urlParams.get('a');
  if (!id)
    return -1
  return parseInt(id);
}

